  authDomain: 'firegram-b75f5.firebaseapp.com',
  projectId: 'firegram-b75f5',
  storageBucket: 'firegram-b75f5.appspot.com',
  messagingSenderId: '953065772353',
  appId: '1:953065772353:web:78ba0e9fedc7f6d5537dc0',
};
