import React from 'react';

function Modal({ selectedImg, setSelectedImg }) {
  const handleClick = (e) => {
    setSelectedImg(null);
  };
  return (
    <div className='backdrop' onClick={handleClick}>
      <img src={selectedImg} alt='enlarged modal pic' />
    </div>
  );
}

export default Modal;
